function init(){setUpModeButtons(),setUpSquares(),reset()}function setUpModeButtons(){for(var e=0;e<modeButtons.length;e++)modeButtons[e].addEventListener("click",function(){modeButtons[0].classList.remove("selected"),modeButtons[1].classList.remove("selected"),modeButtons[2].classList.remove("selected"),this.classList.add("selected"),numSquares="Leicht"===this.textContent?3:"Mittel"===this.textContent?6:9,reset()})}function setUpSquares(){for(var e=0;e<squares.length;e++)squares[e].addEventListener("click",function(){var e=this.style.background;console.log(e,pickedColor),e===pickedColor?(messageDisplay.textContent="Richtig!",resetButton.textContent="Nochmal?",changeColors(pickedColor)):(this.style.background="white",messageDisplay.textContent="Falsch!")})}function reset(){colors=generateRandomColors(numSquares),pickedColor=pickColor(),colorDisplay.textContent=pickedColor,resetButton.textContent="Neue Farben",h1.style.background="#333",messageDisplay.textContent="";for(var e=0;e<squares.length;e++)colors[e]?(squares[e].style.display="block",squares[e].style.background=colors[e]):squares[e].style.display="none"}function changeColors(e){for(var t=0;t<squares.length;t++)squares[t].style.background=e;h1.style.background=e}function pickColor(){var e=Math.floor(Math.random()*colors.length);return colors[e]}function generateRandomColors(e){for(var t=[],o=0;o<e;o++)t.push(randomColor());return t}function randomColor(){var e=Math.floor(256*Math.random()),t=Math.floor(256*Math.random()),o=Math.floor(256*Math.random());return"rgb("+e+", "+t+", "+o+")"}var colors=[],numSquares=6,pickedColor,squares=document.querySelectorAll(".square"),colorDisplay=document.getElementById("colorDisplay"),messageDisplay=document.querySelector("#message"),h1=document.querySelector("h1"),resetButton=document.querySelector("#reset"),modeButtons=document.querySelectorAll(".mode");init(),resetButton.addEventListener("click",function(){reset()});